// 레이더 차트 중심점과 각 꼭지점 좌표
const C = { x: 54.7065, y: 54.3478 };
const AXIS_ENDS = [
  { x: 54.7065, y: 0 },        // 위 (감성지능)
  { x: 106.394, y: 37.5533 },  // 우상 (감정조절)
  { x: 86.6514, y: 98.3162 },  // 우하 (긍정정서)
  { x: 22.7617, y: 98.3162 },  // 좌하 (리더십감성역량)
  { x: 3.0188, y: 37.5533 },   // 좌상 (감정활용)
];

// 레이더 차트 계산 함수
function genRadar(values) {
  const clamp01 = (x) => Math.max(0, Math.min(1, x));
  const M = 5; // 5점 만점

  const verts = values.map((v, i) => {
    const t = clamp01(v / M);
    const e = AXIS_ENDS[i];
    return {
      x: C.x + (e.x - C.x) * t,
      y: C.y + (e.y - C.y) * t,
    };
  });

  const clipD =
    `M${verts[0].x} ${verts[0].y}` +
    verts.slice(1).map((p) => `L${p.x} ${p.y}`).join("") +
    "Z";

  return { verts, clipD };
}




